static void _UpdateCalendarDisplay(WM_HWIN hWin) {
 
  if (!g_TimerActive) return; // if g_TimerActive = 0 --return
  
  Calendar_TypeDef calendar;
  char buffer[10];
  WM_HWIN hItem;
  
  
  get_current_calendar(&calendar);
  
  
  hItem = WM_GetDialogItem(hWin, ID_TEXT_0);
  sprintf(buffer, "%04d", 2000+calendar.year);
  TEXT_SetText(hItem, buffer);
  
 
  hItem = WM_GetDialogItem(hWin, ID_TEXT_2);
  sprintf(buffer, "%02d", calendar.month);
  TEXT_SetText(hItem, buffer);
  
  
  hItem = WM_GetDialogItem(hWin, ID_TEXT_4);
  sprintf(buffer, "%02d", calendar.day);
  TEXT_SetText(hItem, buffer);
  
 
  hItem = WM_GetDialogItem(hWin, ID_TEXT_5);
  sprintf(buffer, "%02d", calendar.hour);
  TEXT_SetText(hItem, buffer);
  
  
  hItem = WM_GetDialogItem(hWin, ID_TEXT_8);
  sprintf(buffer, "%02d", calendar.minute);
  TEXT_SetText(hItem, buffer);
  
  
  hItem = WM_GetDialogItem(hWin, ID_TEXT_10);
  sprintf(buffer, "%02d", calendar.second);
  TEXT_SetText(hItem, buffer);
  
  
  hItem = WM_GetDialogItem(hWin, ID_TEXT_6);
  TEXT_SetFont(hItem, &GUI_Fontchinese);
 
  TEXT_SetText(hItem, get_weekday_name(calendar.weekday));
}

