//闹钟响铃窗口的回调函数
static void _cbAlarmDialog(WM_MESSAGE * pMsg) {
  WM_HWIN hItem;
  int     NCode;
  int     Id;

  switch (pMsg->MsgId) {
  case WM_INIT_DIALOG:
    // 初始化闹钟响铃窗口
    hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_ALARM);
    TEXT_SetFont(hItem, &GUI_Fontchinese40);
    TEXT_SetText(hItem, alarm_come_chinese_40);
    
    hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_CLOSE_RING);
    BUTTON_SetFont(hItem, &GUI_Fontchinese);
    BUTTON_SetText(hItem, back_chinese);
    
     HAL_TIM_PWM_Start(&htim12,TIM_CHANNEL_1);
    __HAL_TIM_SetCompare(&htim12, TIM_CHANNEL_1, 500);
    
    break;
    
  case WM_NOTIFY_PARENT:
    Id    = WM_GetId(pMsg->hWinSrc);
    NCode = pMsg->Data.v;
    
    
    switch(Id) {
    case ID_BUTTON_CLOSE_RING: // 关闭闹钟按钮
      switch(NCode) {
      case WM_NOTIFICATION_CLICKED:
        // 关闭闹钟响铃窗口并返回主窗口
        HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1); 
        WM_DeleteWindow(pMsg->hWin);
        CreateWindow();
        break;
      }
      break;
    }
    break;
    
  default:
    WM_DefaultProc(pMsg);
    break;
  }
}

