// 音乐播放器回调函数
static void _cbPlayerDialog(WM_MESSAGE * pMsg) {
    WM_HWIN hItem;
    int     NCode;
    int     Id;
    static char midi_files[10][50];  // 存储MIDI文件名
    static uint8_t file_count = 0;   // 文件数量计数器

    switch (pMsg->MsgId) {
    case WM_INIT_DIALOG:
        // 初始化播放器窗口
        hPlayerWindow = pMsg->hWin;
        
        // 初始化音乐控制按钮
        hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_PLAY);
        BUTTON_SetFont(hItem, &GUI_Fontchinese);
        BUTTON_SetText(hItem, play_chinese);
        
        hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_PAUSE);
        BUTTON_SetFont(hItem, &GUI_Fontchinese);
        BUTTON_SetText(hItem, pause_chinese);
        
        hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_STOP);
        BUTTON_SetFont(hItem, &GUI_Fontchinese);
        BUTTON_SetText(hItem, stop_chinese);
        
        hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_PREV);
        BUTTON_SetFont(hItem, &GUI_Fontchinese);
        BUTTON_SetText(hItem, previous_chinese);
        
        hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_NEXT);
        BUTTON_SetFont(hItem, &GUI_Fontchinese);
        BUTTON_SetText(hItem, next_chinese);
        
        hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_SONG_INFO);
        TEXT_SetFont(hItem, &GUI_Fontchinese);
        TEXT_SetTextColor(hItem, GUI_BLACK);
        TEXT_SetText(hItem, song_little_star_chinese);
        
        hItem = WM_GetDialogItem(pMsg->hWin, ID_SLIDER_VOLUME);
        SLIDER_SetRange(hItem, 0, 100);
        SLIDER_SetValue(hItem, 80); // 默认音量80%
        SLIDER_SetNumTicks(hItem, 10);
        
        hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_PIANO_BACK);
        BUTTON_SetFont(hItem, &GUI_Fontchinese);
        BUTTON_SetText(hItem, back_chinese);
        
        // 初始化MIDI控件
        hItem = WM_GetDialogItem(pMsg->hWin, ID_LIST_MIDI_FILES);
        LISTBOX_SetFont(hItem, GUI_FONT_16B_ASCII);
        
        hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_SELECTED_FILE);
        TEXT_SetFont(hItem, &GUI_Fontchinese);
        TEXT_SetText(hItem, choose_chinese);
        
        hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_PLAY_MIDI);
        BUTTON_SetFont(hItem, &GUI_Fontchinese);
        BUTTON_SetText(hItem, play_midi_chinese);
        
        hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_STOP_MIDI);
        BUTTON_SetFont(hItem, &GUI_Fontchinese);
        BUTTON_SetText(hItem, stop_chinese);
        
        
        // 初始化MIDI文件数量显示
        hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_MIDI_COUNT);
        TEXT_SetFont(hItem, &GUI_Fontchinese);
        TEXT_SetTextColor(hItem, GUI_GREEN); // 使用绿色显示，更醒目
        
        
        
        
        // 扫描SD卡中的MIDI文件
        ScanMidiFiles(midi_files, &file_count);
        
        
        // 更新文件数量显示
        UpdateMidiFileCountDisplay(pMsg->hWin, file_count);
        
        
        // 填充文件列表
        hItem = WM_GetDialogItem(pMsg->hWin, ID_LIST_MIDI_FILES);
        for (uint8_t i = 0; i < file_count; i++) {
            LISTBOX_AddString(hItem, midi_files[i]);
        }
        LISTBOX_SetAutoScrollV(hItem, 1);
        
        // 初始化播放器状态
        player_state = PLAYER_STOPPED;
        current_note_index = 0;
        
        // 创建定时器用于音乐播放
        WM_CreateTimer(pMsg->hWin, 0, 10, 0); // 10ms定时器
        break;
        
    case WM_TIMER:
        // 音乐播放定时器处理
        if (player_state == PLAYER_PLAYING_NOTE || player_state == PLAYER_IN_GAP) {
            uint32_t current_time = HAL_GetTick();
            uint32_t elapsed_time = current_time - note_start_time;
            
            if (player_state == PLAYER_PLAYING_NOTE) {
                // 检查当前音符是否播放完毕（考虑节奏因子）
                if (elapsed_time >= (uint32_t)(song[current_note_index].duration / tempo_factor)) {
                    // 音符播放完毕，进入停顿状态
                    HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1);
                    player_state = PLAYER_IN_GAP;
                    note_start_time = current_time;
                }
            } else if (player_state == PLAYER_IN_GAP) {
                // 检查停顿是否结束（考虑节奏因子）
                if (elapsed_time >= (uint32_t)(song[current_note_index].gap / tempo_factor)) {
                    // 停顿结束，播放下一个音符
                    current_note_index++;
                    
                    if (current_note_index >= SONG_LENGTH) {
                        // 歌曲结束，回到开头
                        current_note_index = 0;
                        player_state = PLAYER_STOPPED;
                    } else {
                        // 播放下一个音符
                        PlayNote(song[current_note_index]);
                        player_state = PLAYER_PLAYING_NOTE;
                        note_start_time = current_time;
                    }
                }
            }
        }
        WM_RestartTimer(pMsg->Data.v, 10);
        break;
        
    case WM_NOTIFY_PARENT:
        Id    = WM_GetId(pMsg->hWinSrc);
        NCode = pMsg->Data.v;
        switch(Id) {
        case ID_BUTTON_PLAY: // 播放按钮
            switch(NCode) {
            case WM_NOTIFICATION_CLICKED:
                if (player_state != PLAYER_PLAYING_NOTE && player_state != PLAYER_IN_GAP) {
                    player_state = PLAYER_PLAYING_NOTE;
                    current_note_index = 0;
                    note_start_time = HAL_GetTick();
                    PlayNote(song[current_note_index]);
                }
                break;
            }
            break;
            
        case ID_BUTTON_PAUSE: // 暂停按钮
            switch(NCode) {
            case WM_NOTIFICATION_CLICKED:
                if (player_state == PLAYER_PLAYING_NOTE) {
                    player_state = PLAYER_IN_GAP;
                    HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1);
                } else if (player_state == PLAYER_IN_GAP) {
                    player_state = PLAYER_PLAYING_NOTE;
                    // 不需要重新启动PWM，因为已经在停顿状态
                }
                break;
            }
            break;
            
        case ID_BUTTON_STOP: // 停止按钮
            switch(NCode) {
            case WM_NOTIFICATION_CLICKED:
                player_state = PLAYER_STOPPED;
                current_note_index = 0;
                HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1);
                break;
            }
            break;
                    
        case ID_BUTTON_PREV: // 上一首按钮
            switch(NCode) {
            case WM_NOTIFICATION_CLICKED:
                // 这里可以切换到上一首歌曲
                break;
            }
            break;
            
        case ID_BUTTON_NEXT: // 下一首按钮
            switch(NCode) {
            case WM_NOTIFICATION_CLICKED:
                // 这里可以切换到下一首歌曲
                break;
            }
            break;
            
        case ID_SLIDER_VOLUME: // 音量滑块
            switch(NCode) {
            case WM_NOTIFICATION_VALUE_CHANGED:
                // 调整音量（通过改变占空比）
                uint8_t volume = SLIDER_GetValue(pMsg->hWinSrc);
                if (player_state == PLAYER_PLAYING_NOTE) {
                    AdjustVolume(volume);
                }
                break;
            }
            break;
            
        case ID_BUTTON_PIANO_BACK: // 返回按钮
            switch(NCode) {
            case WM_NOTIFICATION_CLICKED:
                // 停止播放
                player_state = PLAYER_STOPPED;
                HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1);
                
                // 关闭播放器窗口
                WM_DeleteWindow(pMsg->hWin);
                CreatePianoWindow(); // 返回到钢琴窗口
                break;
            }
            break;
            
        case ID_LIST_MIDI_FILES: // MIDI文件列表
            switch(NCode) {
            case WM_NOTIFICATION_CLICKED:
                hItem = WM_GetDialogItem(pMsg->hWin, ID_LIST_MIDI_FILES);
                int sel = LISTBOX_GetSel(hItem);
                if (sel >= 0 && sel < file_count) {
                    char text[60];
                    sprintf(text, "%s %s", choose_chinese, midi_files[sel]);
                    
                    hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_SELECTED_FILE);
                    TEXT_SetText(hItem, text);
                }
                break;
            }
            break;
            
        case ID_BUTTON_PLAY_MIDI: // 播放MIDI按钮
            switch(NCode) {
            case WM_NOTIFICATION_CLICKED:
                // 获取选中的文件名并播放
                hItem = WM_GetDialogItem(pMsg->hWin, ID_LIST_MIDI_FILES);
                int sel = LISTBOX_GetSel(hItem);
                if (sel >= 0 && sel < file_count) {
                  f_mount(&SDFatFS, SDPath, 1); // 此时立即挂载
                    USB_MSC_Disable();      // 临时关闭 MSC
                    PlayMidiFile(midi_files[sel]);
                    USB_MSC_Enable();       // 播放结束再恢复
                }
                break;
            }
            break;
            
        case ID_BUTTON_STOP_MIDI: // 停止MIDI按钮
            switch(NCode) {
            case WM_NOTIFICATION_CLICKED:
                midi_state = MIDI_STOPPED;
                HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1);
                break;
            }
            break;
            
            
        // 在回调函数中添加刷新按钮处理
        case ID_BUTTON_REFRESH: // 刷新按钮
            switch(NCode) {
            case WM_NOTIFICATION_CLICKED:
                // 重新扫描MIDI文件
                file_count = 0;
                FRESULT res = ScanMidiFiles(midi_files, &file_count);
                
                // 清空并重新填充文件列表
                hItem = WM_GetDialogItem(pMsg->hWin, ID_LIST_MIDI_FILES);
               // LISTBOX_DeleteAll(hItem);
                
                for (uint8_t i = 0; i < file_count; i++) {
                    LISTBOX_AddString(hItem, midi_files[i]);
                }
                
                // 更新文件数量显示
                UpdateMidiFileCountDisplay(pMsg->hWin, file_count);
                
                // 重置选择状态
                hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_SELECTED_FILE);
                TEXT_SetText(hItem, "Select a MIDI file");
                break;
            }
            break;   
         
             
        }
        break;
        
    default:
        WM_DefaultProc(pMsg);
        break;
    }
}



