static void SetCurrentDateTimeToDropdowns(WM_HWIN hWin) {
  Calendar_TypeDef calendar;
  WM_HWIN hItem;
  
  // 获取当前日历
  get_current_calendar(&calendar);
  
  // 设置日期下拉框
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_DAY);
  DROPDOWN_SetSel(hItem, calendar.day - 1); // 天数从1开始，索引从0开始
  
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_MONTH);
  DROPDOWN_SetSel(hItem, calendar.month - 1); // 月份从1开始，索引从0开始
  
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_YEAR);
  // 查找当前年份的索引
  int current_year = 2000 + calendar.year;
  for (int i = 0; i <= 99; i++) { 
    if (2020 + i == current_year) {
      DROPDOWN_SetSel(hItem, i);
      break;
    }
  }
  
  // 设置时间下拉框
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_HOUR);
  int hour_12 = calendar.hour % 12;
  if (hour_12 == 0) hour_12 = 12;
  DROPDOWN_SetSel(hItem, hour_12 - 1); // 小时从1开始，索引从0开始
  
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_MINUTE);
  DROPDOWN_SetSel(hItem, calendar.minute); // 分钟从0开始，索引也从0开始
  
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_AMPM);
  DROPDOWN_SetSel(hItem, calendar.hour >= 12 ? 1 : 0); // 0=AM, 1=PM
  
  // 设置秒下拉框（新增）
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_SECOND);
  DROPDOWN_SetSel(hItem, calendar.second); // 秒从0开始，索引也从0开始
  
  
}

