static void SaveDateTimeFromDropdowns(WM_HWIN hWin) {
  WM_HWIN hItem;
  Calendar_TypeDef new_calendar;
  
  // 获取日期下拉框的值
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_DAY);
  new_calendar.day = DROPDOWN_GetSel(hItem) + 1; // 索引从0开始，天数从1开始
  
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_MONTH);
  new_calendar.month = DROPDOWN_GetSel(hItem) + 1; // 索引从0开始，月份从1开始
  
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_YEAR);
  int year_index = DROPDOWN_GetSel(hItem);
  int full_year = 2020 + year_index; // 2020-2099
  new_calendar.year = full_year - 2000; // 转换为RTC格式的年份
  
  // 计算星期
  new_calendar.weekday = CalculateWeekday(full_year, new_calendar.month, new_calendar.day);
  
  // 获取时间下拉框的值
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_HOUR);
  int hour_12 = DROPDOWN_GetSel(hItem) + 1; // 索引从0开始，小时从1开始
  
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_MINUTE);
  new_calendar.minute = DROPDOWN_GetSel(hItem); // 索引从0开始，分钟从0开始
  
  // 获取秒下拉框的值
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_SECOND);
  new_calendar.second = DROPDOWN_GetSel(hItem); // 索引从0开始，秒从0开始
  
  hItem = WM_GetDialogItem(hWin, ID_DROPDOWN_AMPM);
  int is_pm = DROPDOWN_GetSel(hItem); // 0=AM, 1=PM
  
  
  // 转换为24小时制
  if (is_pm == 1) { // PM
    new_calendar.hour = (hour_12 % 12) + 12;
    if (new_calendar.hour == 24) new_calendar.hour = 12; // 处理12 PM特殊情况
  } else { // AM
    new_calendar.hour = hour_12 % 12;
    if (new_calendar.hour == 0) new_calendar.hour = 0; // 处理12 AM特殊情况
  }
  
  // 设置秒数为当前秒数（保持连续性）
//  RTC_TimeTypeDef current_time;
//  HAL_RTC_GetTime(&hrtc, &current_time, RTC_FORMAT_BIN);
//  new_calendar.second = current_time.Seconds;
  
  // 设置新的日期时间
  set_calendar(&new_calendar);
  
   // 保存闹钟设置
  SaveAlarmFromDropdowns(hWin);
  
}

