/*********************************************************************
*
*       检查闹钟是否应该触发
*
**********************************************************************
*/
void CheckAlarmTrigger(void) {
  if (!g_AlarmEnable || g_AlarmTriggered) {
    return;
  }
  
  Calendar_TypeDef current_calendar;
  get_current_calendar(&current_calendar);
  
  // 检查小时和分钟是否匹配
  if (current_calendar.hour == g_AlarmHour && current_calendar.minute == g_AlarmMinute) {
    // 避免在同一分钟内重复触发
    if (current_calendar.second == 0) {
      g_AlarmTriggered = 1;
      TriggerAlarm();
    }
  
    
  } else {
    // 重置触发标志，允许下次触发
    g_AlarmTriggered = 0;
  }
}

