// 计算功率谱（实时ADC数据版本）
void Calculate_Power_Spectrum(void)
{
  // 执行FFT计算
  arm_cfft_f32(&arm_cfft_sR_f32_len512, data_in, 0, 1);
  arm_cmplx_mag_f32(data_in, data_out, NPT);
  
  // 归一化处理
  for(int j = 0; j < NPT; j++)
  {
    data_out[j] = data_out[j] / 2;
  }
  
  // 找到峰值频率
  float max_value = 0;
  int max_index = 0;
  
  // 只搜索前NPT/2个点（由于FFT的对称性）
  for(int i = 0; i < NPT/2; i++)
  {
    if(data_out[i] > max_value)
    {
      max_value = data_out[i];
      max_index = i;
    }
  }
  
  // 计算峰值频率（采样率48kHz）
  float peak_frequency = max_index * (44000.0f / NPT);
  
  // 输出峰值频率到串口（可选）
  //printf("Peak Frequency: %.2f Hz at index %d\n", peak_frequency, max_index);
  
  // 在频域窗口中显示峰值频率
  Display_Peak_Frequency(peak_frequency);
  UpdateFreqDisplay(hPianoWin, peak_frequency);
  //Update_Peak_Frequency_Display(peak_frequency);
   
}




