// 自动调整显示范围（对数幅度版本）
void Auto_Adjust_Display_Range(GRAPH_SCALE_Handle hScale)
{
  static uint32_t last_adjust_time = 0;
  uint32_t current_time = HAL_GetTick();
  
  // 每2秒调整一次范围
  if (current_time - last_adjust_time >= 2000)
  {
    // 找到最大和最小dB值
    float32_t minDb = 0.0f;
    float32_t maxDb = -120.0f;
    
    for (int i = 0; i < NUM_BANDS; i++)
    {
      if (bandSpectrum[i] > maxDb) maxDb = bandSpectrum[i];
      if (bandSpectrum[i] < minDb) minDb = bandSpectrum[i];
    }
    
    // 设置新的范围（留一些边距）
    float32_t newMax = maxDb + 5.0f;
    if (newMax > 0.0f) newMax = 0.0f;
    
    float32_t newMin = minDb - 5.0f;
    if (newMin < -120.0f) newMin = -120.0f;
    
    //GRAPH_SCALE_SetRange(hScale, newMin, newMax);
    
    last_adjust_time = current_time;
  }
}

