// 计算定时器配置的函数
void CalculateTimerConfig(float frequency, uint32_t* psc, uint32_t* arr) {
    // 计算基础周期
    float period = 1.0f / frequency;
    
    // 计算所需的定时器计数
    float timer_counts = TIMER_CLOCK_FREQ * period;
    
    // 如果计数超过最大值，需要预分频
    if (timer_counts > MAX_AUTO_RELOAD) {
        *psc = (uint32_t)(timer_counts / MAX_AUTO_RELOAD) + 1;
        *arr = (uint32_t)(timer_counts / *psc) - 1;
    } else {
        *psc = 0; // 不使用预分频
        *arr = (uint32_t)timer_counts - 1;
    }
    
    // 确保值在有效范围内
    if (*psc > MAX_PRESCALER) *psc = MAX_PRESCALER;
    if (*arr > MAX_AUTO_RELOAD) *arr = MAX_AUTO_RELOAD;
    if (*arr < 1) *arr = 1;
}



// 修改播放音符函数以考虑节奏因子
void PlayNote(Note note) {
    uint32_t psc, arr;
    CalculateTimerConfig(note.frequency, &psc, &arr);
    
    // 停止定时器
    HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1);
    
    // 重新配置定时器
    __HAL_TIM_SET_PRESCALER(&htim12, psc);
    __HAL_TIM_SET_AUTORELOAD(&htim12, arr);
    
    // 根据音量设置占空比
    uint32_t compare_value = (arr * note.volume) / 100;
    __HAL_TIM_SET_COMPARE(&htim12, TIM_CHANNEL_1, compare_value);
    
    // 启动定时器
    HAL_TIM_PWM_Start(&htim12, TIM_CHANNEL_1);
}






