// 更新圆形频谱显示（直接绘制在窗口上）
void Update_Circular_Spectrum_Display(WM_HWIN hWin)
{
  // 获取窗口客户区
  GUI_RECT rect;
  WM_GetClientRect(&rect);
  
  int centerX = (rect.x0 + rect.x1) / 2;
  int centerY = (rect.y0 + rect.y1) / 2;
  int radius = circularSpectrumRadius; // 使用可调节的半径
  
  // 清除窗口背景
  GUI_SetColor(GUI_BLACK);
  GUI_FillRect(rect.x0, rect.y0, rect.x1, rect.y1);
  
  // 绘制圆形基线
  GUI_SetColor(GUI_GRAY);
  GUI_DrawCircle(centerX, centerY, radius);
  
  // 绘制同心圆
  for (int i = 1; i <= 3; i++) {
    GUI_DrawCircle(centerX, centerY, radius * i / 4);
  }
  
  // 绘制十字线
  GUI_DrawLine(centerX - radius, centerY, centerX + radius, centerY);
  GUI_DrawLine(centerX, centerY - radius, centerX, centerY + radius);
  
  // 计算频谱数据点的角度间隔
  const int numPoints = NPT / 2; // 使用一半的频谱数据
  const float angleStep = 2 * PI / numPoints;
  
  // 绘制频谱线
  GUI_SetColor(GUI_WHITE);
  
  for (int i = 0; i < numPoints; i++) {
    float angle = i * angleStep;
    float magnitude = data_out[i] / NPT/50;
    
    // 应用缩放因子
    magnitude *= circularSpectrumScale;
    
    // 限制幅度范围
    if (magnitude > 1.0f) magnitude = 1.0f;
    
    // 计算线的终点
    int endX = centerX + (int)(radius * magnitude * cos(angle));
    int endY = centerY + (int)(radius * magnitude * sin(angle));
    
    // 绘制线
    GUI_DrawLine(centerX, centerY, endX, endY);
  }
  
  // 绘制频谱点
  GUI_SetColor(GUI_YELLOW);
  for (int i = 0; i < numPoints; i++) {
    float angle = i * angleStep;
    float magnitude = data_out[i] / NPT;
    
    // 应用缩放因子
    magnitude *= circularSpectrumScale;
    
    // 限制幅度范围
    if (magnitude > 1.0f) magnitude = 1.0f;
    
    // 计算点的位置
    int pointX = centerX + (int)(radius * magnitude * cos(angle));
    int pointY = centerY + (int)(radius * magnitude * sin(angle));
    
    // 绘制点
    GUI_FillCircle(pointX, pointY, 2);
  }
  
  // 添加模式指示文本和范围信息
  GUI_SetColor(GUI_WHITE);
  GUI_SetFont(GUI_FONT_16B_ASCII);
  GUI_DispStringAt("Circular Spectrum", centerX - 60, rect.y1 - 20);
  
  // 显示当前半径
  char radiusInfo[20];
  sprintf(radiusInfo, "Radius: %d", circularSpectrumRadius);
  GUI_DispStringAt(radiusInfo, centerX - 40, rect.y1 - 40);
   // 刷新图表
  WM_InvalidateWindow(hWin);
}

