// MIDI音符编号转换为频率(Hz)
float MidiNoteToFrequency(uint8_t note) {
    // MIDI音符69(A4) = 440Hz
    return 440.0f * powf(2.0f, (note - 69) / 12.0f);
}


uint32_t ReadVariableLength(uint8_t* data, uint32_t* offset, uint32_t max_size) {
    uint32_t value = 0;
    uint8_t byte;
    uint8_t count = 0;

    do {
        if (*offset >= max_size) {
            printf("VLQ overflow at offset=%lu/%lu\n", *offset, max_size);
            return value;
        }

        byte = data[(*offset)++];
        value = (value << 7) | (byte & 0x7F);

        if (++count > 8) {
            printf("VLQ too long (offset=%lu)\n", *offset);
            break;
        }
    } while (byte & 0x80);

    return value;
}

// 解析MIDI文件头
FRESULT ParseMidiHeader(FIL* file, uint16_t* format, uint16_t* tracks, uint16_t* division) {
    UINT bytes_read;
    uint8_t header[14];
    
    // 读取文件头
    retSD = f_read(file, header, 14, &bytes_read);
    if (retSD != FR_OK || bytes_read != 14) {
       // printf("Failed to read MIDI header: %d\n", retSD);
        return FR_DISK_ERR;
    }
    
    // 检查MIDI文件标识
    if (memcmp(header, "MThd", 4) != 0) {
        //printf("Invalid MIDI file format\n");
        return FR_INVALID_OBJECT;
    }
    
    // 读取格式、轨道数和分度值
    *format = (header[8] << 8) | header[9];
    *tracks = (header[10] << 8) | header[11];
    *division = (header[12] << 8) | header[13];
    
    //printf("MIDI format: %d, Tracks: %d, Division: %d\n", *format, *tracks, *division);
    return FR_OK;
}



// ====== 流式播放单轨，截断前30秒 ======
FRESULT ParseMidiTrack(FIL *file) {
    FRESULT res;
    UINT    br;
    uint8_t hdr[8];

    // 读 MTrk 头
    res = f_read(file, hdr, 8, &br);
    if (res != FR_OK || br != 8) return FR_DISK_ERR;
    if (memcmp(hdr, "MTrk", 4) != 0) {
        printf("Invalid MTrk header\n");
        return FR_INVALID_OBJECT;
    }

    uint32_t chunk_size = (hdr[4] << 24) | (hdr[5] << 16) | (hdr[6] << 8) | hdr[7];
    DWORD chunk_start = f_tell(file);
    DWORD chunk_end   = chunk_start + chunk_size;

    if (chunk_end > f_size(file)) {
        printf("Chunk end beyond file size, correcting\n");
        chunk_end = f_size(file);
    }

    // 默认速度/分辨率
    if (midi_ppq == 0)    midi_ppq = 480;
    if (midi_tempo == 0)  midi_tempo = 500000;

    uint8_t running_status = 0;
    double elapsed_ms = 0.0;   // ? 已播放时间（毫秒）

    while (f_tell(file) < chunk_end && midi_state == MIDI_PLAYING) {

        // 1?? 读 delta
        uint32_t delta = ReadVLQ_File(file, chunk_end, &res);
        if (res != FR_OK) break;

        // 计算当前事件间隔（毫秒）
        double ms_per_tick = ((double)midi_tempo / (double)midi_ppq) / 1000.0;
        uint32_t wait_ms = (uint32_t)(delta * ms_per_tick);
        elapsed_ms += wait_ms;

        // 若累计时间超过 30 秒则提前结束
        if (elapsed_ms > 30000.0) {
            printf("[MIDI] 30s reached, stop early.\n");
            break;
        }

        // 2?? 读状态字节（带运行状态）
        if (f_tell(file) >= chunk_end) break;
        uint8_t status = f_getc1(file, &res);
        if (res != FR_OK) break;

        if (status < 0x80) {
            if (running_status == 0) return FR_INVALID_OBJECT;
            f_lseek(file, f_tell(file) - 1);
            status = running_status;
        } else {
            if ((status & 0xF0) != 0xF0)
                running_status = status;
            else
                running_status = 0;
        }

        uint8_t hi = status & 0xF0;

        // 3?? 处理事件
        if (hi >= 0x80 && hi <= 0xE0) {
            uint8_t data1 = f_getc1(file, &res);
            if (res != FR_OK) break;

            if (hi == 0xC0 || hi == 0xD0) {
                // 1字节事件跳过
            } else {
                uint8_t data2 = f_getc1(file, &res);
                if (res != FR_OK) break;

                if (hi == 0x90) { // Note On
                    if (data2) {
                        PlayMidiNote(data1, data2);
                        HAL_Delay(80);
                    }
                } else if (hi == 0x80) {
                    StopMidiNote(data1);
                }
            }
        }
        else if (status == 0xFF) {
            uint8_t meta_type = f_getc1(file, &res);
            if (res != FR_OK) break;

            uint32_t meta_len = ReadVLQ_File(file, chunk_end, &res);
            if (res != FR_OK) break;

            if (meta_type == 0x2F) { // End of track
                if (f_tell(file) + meta_len > chunk_end)
                    meta_len = chunk_end - f_tell(file);
                f_lseek(file, f_tell(file) + meta_len);
                break;
            } else if (meta_type == 0x51 && meta_len == 3) {
                uint8_t t[3]; UINT rb=0;
                res = f_read(file, t, 3, &rb);
                if (res != FR_OK || rb != 3) break;
                midi_tempo = (t[0]<<16)|(t[1]<<8)|t[2];
            } else {
                f_lseek(file, f_tell(file) + meta_len);
            }
        }
        else if (status == 0xF0 || status == 0xF7) {
            uint32_t syx_len = ReadVLQ_File(file, chunk_end, &res);
            if (res != FR_OK) break;
            f_lseek(file, f_tell(file) + syx_len);
        }

        // 4?? 节奏等待（分片延时）
        while (wait_ms && midi_state == MIDI_PLAYING) {
            uint32_t slice = (wait_ms > 10U) ? 10U : wait_ms;
            HAL_Delay(slice);
            wait_ms -= slice;
        }
    }

    printf("[MIDI] Parse end at %.2fs\n", elapsed_ms / 1000.0);
    return FR_OK;
}



// ================= 播放MIDI文件 =================
FRESULT PlayMidiFile(const char* filename) {
    uint16_t format, tracks, division;
    FRESULT res;
    char filepath[64];

    // 根目录文件
    snprintf(filepath, sizeof(filepath), "0:/%s", filename);

    res = f_open(&SDFile, filepath, FA_READ);
    if (res != FR_OK) {
        //printf("Open MIDI failed: %d\n", res);
        return res;
    }

    res = ParseMidiHeader(&SDFile, &format, &tracks, &division);
    if (res != FR_OK) {
        f_close(&SDFile);
        //printf("Parse header failed: %d\n", res);
        return res;
    }

    // 仅支持 PPQ（忽略 SMPTE）
    midi_ppq = (uint16_t)(division & 0x7FFF);
    if (midi_ppq == 0) midi_ppq = 480; // 兜底

    midi_tempo = 500000;        // 每次播放重置默认 Tempo，防止上一次残留
    midi_state = MIDI_PLAYING;

    //printf("Playing MIDI: %s, format=%u, tracks=%u, ppq=%u\n",
          // filename, format, tracks, midi_ppq);

    // 只播第一轨（单轨要求）
    res = ParseMidiTrack(&SDFile);

    midi_state = MIDI_STOPPED;
    HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1);
    f_close(&SDFile);

    //printf("Playback finished, res=%d\n", res);
    return res;
}



