// 扫描SD卡中的MIDI文件
FRESULT ScanMidiFiles(char files[][50], uint8_t* count) {
    FRESULT res;
    DIR dir;
    FILINFO fno;


    *count = 0;

    // 打开根目录（根据你的SDPath调整）
    res = f_opendir(&dir, "midi");  
    if (res != FR_OK) {
        //printf("Failed to open directory: %d\n", res);
        return res;
    }
    
    int i = 100;

    // 遍历目录
    while (i) {
        res = f_readdir(&dir, &fno);
        if (res != FR_OK || fno.fname[0] == 0) break;

        // 跳过目录
        if (fno.fattrib & AM_DIR) continue;

        // 选择正确的文件名指针
        char *filename = fno.fname;
        if (!filename || filename[0] == 0) continue;

        // 检查文件扩展名
        char *ext = strrchr(filename, '.');
        if (ext && (strcasecmp(ext, ".mid") == 0 || strcasecmp(ext, ".midi") == 0)) {
            if (*count < 10) {
                strncpy(files[*count], filename, 50);
                files[*count][49] = '\0';
                (*count)++;
            }
        }
        i--;
       // HAL_Delay(1);
    }

    f_closedir(&dir);
    //printf("Found %d MIDI files\n", *count);
    return FR_OK;
}

// ================= 播放音符 =================
void PlayMidiNote(uint8_t note, uint8_t velocity) {
    float freq = MidiNoteToFrequency(note);
    uint32_t psc, arr;
    CalculateTimerConfig(freq, &psc, &arr);

    HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1);
    __HAL_TIM_SET_PRESCALER(&htim12, psc);
    __HAL_TIM_SET_AUTORELOAD(&htim12, arr);
    HAL_Delay(1); // 稍微等1ms避免旧波形残留
    uint32_t duty = (arr * velocity) / 127;
    __HAL_TIM_SET_COMPARE(&htim12, TIM_CHANNEL_1, duty);
    HAL_TIM_PWM_Start(&htim12, TIM_CHANNEL_1);
}



