// 读取 1 字节；失败时把 *perr 置为错误码
static uint8_t f_getc1(FIL *file, FRESULT *perr) {
    UINT br = 0;
    uint8_t b = 0;
    *perr = f_read(file, &b, 1, &br);
    if (*perr != FR_OK || br != 1) *perr = FR_DISK_ERR;
    return b;
}

// 从文件读取 VLQ（带 chunk_end 边界保护）
static uint32_t ReadVLQ_File(FIL *file, DWORD chunk_end, FRESULT *perr) {
    uint32_t val = 0;
    uint8_t  byte = 0;
    uint8_t  count = 0;

    do {
        if (f_tell(file) >= chunk_end) { *perr = FR_DISK_ERR; return val; }
        byte = f_getc1(file, perr);
        if (*perr != FR_OK) return val;
        val = (val << 7) | (byte & 0x7F);
        if (++count > 4) { // 非法的超长 VLQ：尽量恢复到下个非续字节
            printf("Invalid VLQ (>4 bytes) at pos=%lu\n", f_tell(file));
            while ((byte & 0x80) && f_tell(file) < chunk_end) {
                byte = f_getc1(file, perr);
                if (*perr != FR_OK) break;
            }
            break;
        }
    } while (byte & 0x80);

    return val;
}


float FindMainFreq(float *fft_mag, float freq_step, uint16_t size)
{
    float max_val = 0;
    uint16_t max_idx = 0;
    for (uint16_t i = 1; i < size/2; i++)
    {
        if (fft_mag[i] > max_val)
        {
            max_val = fft_mag[i];
            max_idx = i;
        }
    }
    return max_idx * (44000.0f / NPT);
}


void Buzzer_PlayFreq(float freq)
{
    if (freq < 20 || freq > 5000) {
        HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1);
        return;
    }
    uint32_t arr = (uint32_t)(SystemCoreClock / (htim12.Init.Prescaler + 1) / freq);
    __HAL_TIM_SET_AUTORELOAD(&htim12, arr);
    __HAL_TIM_SET_COMPARE(&htim12, TIM_CHANNEL_1, arr / 2);
    HAL_TIM_PWM_Start(&htim12, TIM_CHANNEL_1);
}


void Playback_RecordedFreq(void)
{
    if (freq_count == 0) return;

    is_playing = 1;
    for (uint16_t i = 0; i < freq_count; i++)
    {
        Buzzer_PlayFreq(freq_record[i]);
        GUI_Delay(SAMPLE_INTERVAL_MS+50);
    }
    HAL_TIM_PWM_Stop(&htim12, TIM_CHANNEL_1);
    is_playing = 0;
}

void Process_AudioPitch(void)
{
    static uint32_t last_tick = 0;

    if (is_recording && HAL_GetTick() - last_tick >= SAMPLE_INTERVAL_MS)
    {
        last_tick = HAL_GetTick();

        if (fft_data_ready)
        {
            fft_data_ready = 0;
            Calculate_Power_Spectrum(); // 计算data_out

            float freq = FindMainFreq(data_out, 44000.0f / NPT, NPT);
            if (freq_count < MAX_RECORD_LEN)
                freq_record[freq_count++] = freq;

            char str[32];
            sprintf(str, "%s: %.1f Hz", frequence_chinese, freq);
            GUI_DispStringAt(str, 10, 240);
            Display_Peak_Frequency(freq);
            
        }
    }
}

